import { createGlobalStyle } from 'styled-components'

export const GlobalStyle = createGlobalStyle`

    :root{
        --maxWidth:1280px;
        --white:#fff;
        --lightGrey:#eee;
        --midGrey:#353535;
        --darkGrey:#1c1c1c;
        --fontSuperBig:2.5rem;
        --fontBig:1.5rem;
        --fontMid:1.2rem;
        --fontSmall:1rem;
        --gradColor:linear-gradient(to bottom right, black, indigo);
    }

    * {
        font-family:oswald;
    }

    body {
        margin:0;
        padding:0;
    }


    ::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-thumb {
  background: #888;
  border-radius: 20px;
}

::-webkit-scrollbar-thumb:hover {
  background: #555; 
}
::-webkit-scrollbar-track {
  background: #f1f1f1;
  border-radius: 20px;
}


      
`;
