import React, { useState, useRef, useEffect } from "react";
import { useHistory } from "react-router-dom";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import axios from "axios";
import "./style.css";

const CroppedImageUpload = () => {
  const [upImg, setUpImg] = useState(null);
  const [userData, setUserData] = useState({});
  const History = useHistory();

  useEffect(() => {
    try {
      async function fetchData() {
        const res = await axios.get("http://localhost:3001/Profile");
        setUserData(res.data);
      }
      fetchData();
    } catch (err) {
      History.push("/Login");
    }
  }, []);

  const imgRef = useRef(null);
  const canvasRef = useRef(null);
  const [crop, setCrop] = useState({ unit: "px", width: 500, aspect: 1 / 1 });
  const croppedImage = useRef(null);

  const onSelectFile = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      console.log(e.target.files[0]);
      const reader = new FileReader();
      reader.addEventListener("load", () => setUpImg(reader.result));
      reader.readAsDataURL(e.target.files[0]);
    }
  };

  const onLoad = (img) => {
    imgRef.current = img;
  };

  const onCropComplete = (crop) => {
    makeClientCrop(crop);
  };

  const makeClientCrop = async (crop) => {
    if (imgRef.current && crop.width && crop.height) {
      croppedImage.current = await getCroppedImg(
        imgRef.current,
        crop,
        "newFile.jpeg"
      );
    }
  };

  const getCroppedImg = (image, crop, fileName) => {
    if (!canvasRef.current || !imgRef.current) {
      return;
    }
    const canvas = canvasRef.current;
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    const ctx = canvas.getContext("2d");
    const pixelRatio = window.devicePixelRatio;
    canvas.width = crop.width * pixelRatio;
    canvas.height = crop.height * pixelRatio;

    ctx.setTransform(pixelRatio, 0, 0, pixelRatio, 0, 0);
    ctx.imageSmoothingQuality = "high";
    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return new Promise((resolve, reject) => {
      // canvas.toDataURL("image/jpeg");
      canvas.toBlob((blob) => {
        if (!blob) {
          //reject(new Error('Canvas is empty'));
          console.error("Canvas is empty");
          return;
        }
        blob.name = fileName;
        resolve(blob);
      }, "image/jpeg");
    });
  };

  const handleSubmit = (e) => {
    try {
      e.preventDefault();
      //here get name of image in place of "name"
      var file = new File([croppedImage.current], "name");
      const formData = new FormData();
      formData.append("file", file);
      // formData.append("userData", userData);
      console.log(formData);
      axios.post("http://localhost:3001/selectImage", formData).then((res) => {
        // then print response status
        if (res.status !== 500) {
          // console.log(res.data);
          // setshowImage(res.data);
          History.push("/Home");
        }
      });
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="imagecropper">
      <form>
        <div className="input-container">
          <input
            accept="image/*"
            id="profile-pic"
            onChange={onSelectFile}
            name="image"
            type="file"
          />
        </div>
        <div className="main-image-container">
          <div className="crop-container">
            <ReactCrop
              src={upImg}
              onImageLoaded={onLoad}
              crop={crop}
              onChange={(c) => setCrop(c)}
              onComplete={onCropComplete}
            />
          </div>
          <div className="cropped-image-container">
            <canvas ref={canvasRef} />
          </div>
        </div>
        {upImg && (
          <div className="upload-button-container">
            <button className="upload-button" onClick={handleSubmit}>
              Upload
            </button>
          </div>
        )}
      </form>

      {/* heres how to get image back
         <div>
        <h1>{showImage}</h1>
        <img
          src={`http://localhost:3001/inProcessImages/${showImage}`}
          alt="getting back"
        />
      </div> */}
    </div>
  );
};

export default CroppedImageUpload;
