import React, { useState } from "react";
import axios from "axios";
import { Wrapper,File,Body } from "./Post.styles";
import Navbar from "../Navbar/Navbar";
import styled from "styled-components";


const Post = () => {
  const [selectedFile, setSelectedFile] = useState("");
  const [responseFile, setResponseFile] = useState("");
  const [path, setPath] = useState('');
  var before = `http://localhost:3001/inProcessImages/${responseFile}`;
  var after = `http://localhost:3001/${responseFile}`;

  const onchangehandler = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const onClickHandler = async (e) => {
    let res;
    e.preventDefault();
    const data = new FormData();
    data.append("file", selectedFile);
    res =
      await axios.post("http://localhost:3001/selectImage", data)
      // then print response status
    if (res.status !== 500 && res) {
      await setResponseFile(res.data);
      await setPath(before);
      console.log(path);
        // setResponseFile(res.filename);
      }
  };

  const handleChange = async (event) => {
    let response;
    event.preventDefault();
     response =
      await axios.post('http://localhost:3001/Change', {
        'responseFile' : responseFile
      });
    await setResponseFile(response.data);
    await setPath(after);
    console.log(path);
  }

  return (
    <Body>
     <Navbar /> 
      <File>
        <div>
          <p style={{ 'margin': "0px" }}>Drag your photos and videos here</p>
          <h6>OR</h6>
          <form>
            <input
              type="file"
              id="myFile"
              className='File-style'
              name="filename"
              onChange={(event) => {
                onchangehandler(event);
              }}
            />
            <button type="submit">
              <h3 onClick={onClickHandler}>
                Upload
              </h3>
            </button>
          </form>
        </div>
      </File>
      <hr/>
      <Wrapper>
        <div className="Styles">
        <form>
          <button onClick = {handleChange}>Color grade</button>
        </form>
        </div>
        <div className="image" style ={{'textAlign':'center'}}>
          {responseFile?<img
            src={path}
            style = {{'height':'auto','width':'90%','marginTop':'20px'}}
          />:<h1>Uploaded Image</h1>}
        </div>
        <div className="Properties">
          
        </div>
      </Wrapper>
      <hr/>
    </Body>
  );
};

export default Post;
