import React, { useEffect, useState } from "react";

import { useHistory } from "react-router";

import { Body, Wrapper, Content, Image, Info, Icons } from "./Profile.style";
import axios from "axios";

import IMAGE from '../../images/navback.jpg';
import Navbar from "../Navbar/Navbar";
import PopUp from "../PopUp/PopUp";

const Profile = () => {

    const [userData, setUserData] = useState({});
    const [posts, setPosts] = useState([]);
    const [profile, setProfile] = useState('');
    const [popup, setPopup] = useState(false);
    const [imageData, setImageData] = useState('');
    const History = useHistory();

    useEffect(async () => {
        try {
            const res = await axios.get('http://localhost:3001/Profile');
            setUserData(res.data);
            setPosts(res.data.post);
        } catch (err) {
            History.push('/Login');
        };
    }, [])

    const Update = async (event) => {
        try {
            let res;
            event.preventDefault();
            const data = new FormData();
            data.append("file", profile);
            res =
                await axios.post("http://localhost:3001/Profile", data)
            // then print response status
            if (res.status !== 500 && res) {
                alert(res.data);
                window.location.reload();
            }
        } catch (err) {
            console.log(err);
        }
    }

    const ProfileImg = (e) => {
        setProfile(e.target.files[0]);
    }

    const Popup = (data) => {
        setPopup(true);
        setImageData(data);
    }

    return <>
        <Navbar />
        <Body>
            <Wrapper>
                {profile ? <button onClick={Update} style={{ 'background': 'aqua', 'border': 'none', 'outline': 'none' }}>Update</button> : ''}
                <div className='edit-icon'>
                    <a href='##'><button><i className="fas fa-trash" style={{ 'color': 'Red' }}></i></button></a>
                </div>
                <Content>
                    <Image>
                        <label className="-label" for="file">
                            <span className="glyphicon glyphicon-camera"></span>
                            <span>Change Image</span>
                        </label>
                        <input id="file" type="file" onChange={ProfileImg} />
                        <img src={userData.profile ? `http://localhost:3001/ProfileImages/${userData.profile}` : IMAGE} id="output" width="200" />

                    </Image>
                    {/* </input> */}
                    {/* <div className = 'backImage'><Image src={IMAGE} /></div> */}
                    <Info>
                        <div>
                            <h1>{userData.userName}</h1>
                            <hr style={{ 'width': '20rem' }} className='line' />
                            <div className='info-split'>
                                <div>
                                    <h5>posts</h5>
                                    <h5>Followers</h5>
                                    <h5>Following</h5><br />

                                </div>
                                <div>
                                    <h5>1</h5>
                                    <h5>50</h5>
                                    <h5>47</h5>
                                </div>
                            </div>
                        </div>
                    </Info>
                </Content>
                <hr />
                <Icons>
                    <i className="fas fa-images fa-2x hover-underline-animation" title='Posts'></i>
                    <i className="fas fa-users fa-2x hover-underline-animation" title='Followers'></i>
                    <i className="fas fa-user-friends fa-2x hover-underline-animation" title='Following'></i>
                </Icons>
                <hr className='hover-underline-animation' />
                <div className='Grid'>
                    {posts.map((post, index) => (
                        <div id={index} onClick={() =>  Popup(post)  }>
                            <img className='Post-style' src={`http://localhost:3001/uploadedImages/${post.image}`} alt='posts' />
                        </div>
                    ))}
                </div>
                {console.log(imageData)}
                {
                    popup ? (                        
                        <PopUp onClose={() => setPopup(false)}>
                            <img src={`http://localhost:3001/uploadedImages/${imageData.image}`} alt='post' className='postImg'/>
                            <div className='Info'>
                                <div>{userData.userName}</div>
                                <div>{imageData.description}</div>
                                <div className='Comment'>
                                    <textarea rows='1'></textarea>
                                </div>
                            </div>
                        </PopUp>
                    ) :
                        ''
                }

            </Wrapper>
        </Body>
    </>
}

export default Profile;
